import React, { useEffect, useState } from 'react'
import axios from 'axios'
import './SalesComponent.css' // optional, for styling

export default function SalesComponent() {
  const [sales, setSales] = useState([])
  const [totalRevenue, setTotalRevenue] = useState(0)
  const [mostSoldProduct, setMostSoldProduct] = useState(null)

  useEffect(() => {
    axios.get('http://localhost:3000/sales')
      .then(res => {
        const data = res.data
        setSales(data)

        // // Calculate total revenue
        // const total = data.reduce((sum, sale) => sum + sale.totalAmount, 0)
        // setTotalRevenue(total)

        // Flatten all products and calculate frequency
        const productMap = {}
        data.forEach(sale => {
          sale.products.forEach(product => {
            if (!productMap[product.name]) {
              productMap[product.name] = product.count
            } else {
              productMap[product.name] += product.count
            }
          })
        })

        // Find most sold
        const maxProduct = Object.entries(productMap).reduce((max, curr) =>
          curr[1] > max[1] ? curr : max, ["", 0]
        )
        setMostSoldProduct({ name: maxProduct[0], count: maxProduct[1] })

      })
      .catch(err => console.error("Failed to fetch sales:", err))
  }, [])

  return (
    <div className="analytics-container">
      <h1>Sales Analytics</h1>
      <p><strong>Total Revenue:</strong> ₹{totalRevenue}</p>
      <p><strong>Total Bills:</strong> {sales.length}</p>
      {mostSoldProduct && (
        <p><strong>Most Sold Product:</strong> {mostSoldProduct.name} ({mostSoldProduct.count} sold)</p>
      )}

      <h2>Sales History</h2>
      <table>
        <thead>
          <tr>
            <th>Date</th>
            <th>Customer</th>
            <th>Products</th>
            <th>Total</th>
          </tr>
        </thead>
        <tbody>
          {sales.map((sale, index) => (
            <tr key={index}>
              <td>{new Date(sale.date).toLocaleString()}</td>
              <td>{sale.customer.name}</td>
              <td>
                {sale.products.map(p => (
                  <div key={p.id}>{p.name} × {p.count}</div>
                ))}
              </td>
              <td>₹{sale.totalAmount}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  )
}
