import React, {useEffect, useState } from 'react'
import axios from 'axios'

export default function Product() {
    const [product,setproduct]=useState([])
    const[editproduct,seteditproduct]=useState({
        id:0,
        name:"",
        total:0,
        price:0
    })
    const [click,setclick]=useState(false)
    const [addprouct,setaddproduct]=useState({
        id:0,
        name:"",
        total:0,
        price:0
    })
    const handleclick=()=>{
        setclick(prev=>!prev)
    }
    //get
    useEffect(()=>{
        axios.get("http://localhost:3000/product")
        .then(res=>{
            setproduct(res.data)
        })
        .catch(err=>console.log(err))
    },[click])

    //add product
    const handleaddproduct=(event)=>{
        event.preventDefault()
      axios.post('http://localhost:3000/product',addprouct)
        .then(res=>{
            console.log(res)
        })
        .catch(err=>console.log(err))
        handleclick()
    }
    const handlechange=(event)=>{
        const{name,value}=event.target
        setaddproduct(pre=>({...pre,[name]:value}))
    }

    //delete
    const handeleDelete=(id)=>{
        axios.delete(`http://localhost:3000/product/${id}`)
        .then(res=>{
            console.log(res)
        })
        .catch(err=>console.log(err))
        handleclick()
    }
    //edit

    const handleeditsubmit=(id)=>{
        axios.put(`http://localhost:3000/product/${id}`,editproduct)
        .then(res=>{
            console.log(res)
        })
        .catch(err=>console.log(err))
    }
    const handleeditchange=(event)=>{
        const{name,value}=event.target
        seteditproduct(prev=>({...prev,[name]:value}))
    }
    const handleedit=(data)=>{
        const{id,name,price,image}=data
        setpopupdata({id,name,price,image})
    }
  return (
    <div>
        <h1>Product page</h1>
        <form  onSubmit={handleaddproduct}>
            <h1>Add Product</h1>
            <label>Name Of The Product</label>
            <input type="text" name='name' value={product.name} onChange={handlechange}></input><br/>
            <label>Total Product</label>
            <input type="number" name='total' value={product.total} onChange={handlechange}></input><br/>
            <label>Product Price</label>
            <input type="number" onChange={handlechange} name='price' value={product.price}></input><br/>
            <button type="submit">submit</button>
        </form>
        <table>
            <thead>
                <tr>
                    <th>Id</th>
                    <th>Name</th>
                    <th>Total</th>
                    <th>price</th>
                    <th>action</th>
                </tr>
            </thead>
            <tbody>
               {product.map((x)=>(
                    <tr key={x.id}>
                        <td>{x.id}</td>
                        <td>{x.name}</td>
                        <td>{x.total}</td>
                        <td>{x.price}</td>
                        <td>
                        <button onClick={()=>{handleedit(x)}}>edit</button>
                        <button onClick={()=>handeleDelete(x.id)}>delete</button>
                        </td>
                        </tr>
                    ))}
            </tbody>
        </table>
    <form onSubmit={()=>handleeditsubmit(editproduct.id)}>
        <label>Name Of The Product</label>
            <input type="text" name='name' value={product.name} onChange={handleeditchange}></input><br/>
            <label>Total Product</label>
            <input type="number" name='total' value={product.total} onChange={handleeditchange}></input><br/>
            <label>Product Price</label>
            <input type="number" onChange={handleeditchange} name='price' value={product.price}></input><br/>
            <button type="submit">submit</button>
        </form> 
    </div>
  )
}
