import React, { useState } from 'react'
import axios from 'axios'
import './Billing1.css'

export default function Billing() {
  const [ProductId, setProductId] = useState(0)
  const [productCount, setProductCount] = useState(0)
  const [editMode, setEditMode] = useState(false)
  const [editId, setEditId] = useState(null)

  const [User, setUser] = useState({
    name: "",
    address: "",
    list: [],
    totalAmount: 0
  })

  const onchageUser = (e) => {
    const { name, value } = e.target
    setUser(prev => ({ ...prev, [name]: value }))
  }

  const HandlesAdd = (id) => {
    if (editMode) {
      setUser(prev => {
        const updatedList = prev.list.map(item =>
          item.id === editId ? { ...item, count: productCount } : item
        )
        const updatedTotal = updatedList.reduce((sum, item) => sum + item.price * item.count, 0)
        return { ...prev, list: updatedList, totalAmount: updatedTotal }
      })
      setEditMode(false)
      setEditId(null)
    } else {
      axios.get(`http://localhost:3000/product/${id}`).then(res => {
        const product = res.data

        if (product.total === 0) {
          alert("Out of stock!")
          return
        }

        if (productCount > product.total) {
          alert(`Only ${product.total} in stock`)
          return
        }

        const newItem = {
          id: product.id,
          name: product.name,
          count: productCount,
          price: product.price
        }

        setUser(prev => {
          const updatedList = [...prev.list, newItem]
          const updatedTotal = updatedList.reduce((sum, item) => sum + item.price * item.count, 0)
          return { ...prev, list: updatedList, totalAmount: updatedTotal }
        })

        // reduce stock
        axios.put(`http://localhost:3000/product/${id}`, {
          ...product,
          total: product.total - productCount
        })
      })
    }
  }

  const handleDelete = (id) => {
    setUser(prev => {
      const updatedList = prev.list.filter(item => item.id !== id)
      const updatedTotal = updatedList.reduce((sum, item) => sum + item.price * item.count, 0)
      return { ...prev, list: updatedList, totalAmount: updatedTotal }
    })
  }

  const handleEdit = (item) => {
    setProductId(item.id)
    setProductCount(item.count)
    setEditMode(true)
    setEditId(item.id)
  }

  const handleSubmitBill = () => {
    if (!User.name || !User.address || User.list.length === 0) {
      alert("Please complete the bill")
      return
    }

    const billData = {
      customer: {
        name: User.name,
        address: User.address
      },
      products: User.list,
      totalAmount: User.totalAmount,
      date: new Date().toISOString()
    }

    axios.post("http://localhost:3000/sales", billData)
      .then(() => {
        alert("Bill submitted")
        setUser({
          name: "",
          address: "",
          list: [],
          totalAmount: 0
        })
        setProductId(0)
        setProductCount(0)
      })
      .catch(err => console.log(err))
  }

  return (
    <div className="container">
      <form onSubmit={e => e.preventDefault()}>
        <label>Name</label>
        <input value={User.name} name='name' onChange={onchageUser} /><br />
        <label>Address</label>
        <textarea name='address' value={User.address} onChange={onchageUser}></textarea><br />
        <label>Product ID</label>
        <input value={ProductId} type='number' onChange={e => setProductId(Number(e.target.value))} /><br />
        <label>Product Count</label>
        <input value={productCount} type='number' onChange={e => setProductCount(Number(e.target.value))} /><br />
        <button type="button" onClick={() => HandlesAdd(ProductId)}>{editMode ? "Update" : "Add"}</button>
      </form>

      <h2>Customer: {User.name}</h2>
      <h3>Address: {User.address}</h3>

      <table>
        <thead>
          <tr><th>Name</th><th>Price</th><th>Count</th><th>Actions</th></tr>
        </thead>
        <tbody>
          {User.list.map((item, i) => (
            <tr key={i}>
              <td>{item.name}</td><td>{item.price}</td><td>{item.count}</td>
              <td>
                <button onClick={() => handleEdit(item)}>Edit</button>
                <button onClick={() => handleDelete(item.id)}>Delete</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>

      <h2>Total: â‚¹{User.totalAmount}</h2>
      <button type="button" onClick={handleSubmitBill}>Submit Bill</button>
    </div>
  )
}
