import axios from 'axios'
import React, { useState } from 'react'
import {useSearchParams } from 'react-router-dom'

export default function SignUppage(props) {
    const [searchParams] = useSearchParams();
    
    const sign=searchParams.get('type');
    const [employee, setEmployee] = useState({
        name: "",
        password: ""
    })
    const [manager, setmanager] = useState({
        name: "",
        password: ""
    })

    const onChangeEmployee = (event) => {
        const { name, value } = event.target
        setEmployee(prev => ({ ...prev, [name]: value }))
    }
    const onChangeManager = (event) => {
        const { name, value } = event.target
        setmanager(prev => ({ ...prev, [name]: value }))
    }

    const registerEmployee = (event) => {
        event.preventDefault()
        axios.post("http://localhost:3000/EmployeeLogin", employee)
            .then(res => {
                console.log(res)
                alert("Register successful")

                setEmployee({ name: "", password: "" }) 
            })
            .catch(err => {
                console.error(err)
                alert("Registration failed")
            })
    }
    const registerManager = (event) => {
        event.preventDefault()
        axios.post("http://localhost:3000/ManagerLogin", manager)
            .then(res => {
                console.log(res)
                alert("Register successful")
                setmanager({ name: "", password: "" })
            })
            .catch(err => {
                console.error(err)
                alert("Registration failed")
            })
    }

    return (
        <div className="container">
            {
                sign==='true'?<div>
                <h1>Employee Registration</h1>
            <form onSubmit={registerEmployee}>
                <label>Name</label>
                <input
                    value={employee.name}
                    name="name"
                    onChange={onChangeEmployee}
                    required
                />
                <label>Password</label>
                <input
                    type="password"
                    value={employee.password}
                    name="password"
                    onChange={onChangeEmployee}
                    required
                />
                <button type="submit">Register</button>
            </form>
                </div>:
                <div>
                <h1>Manager Registration</h1>
            <form onSubmit={registerManager}>
                <label>Name</label>
                <input
                    value={manager.name}
                    name="name"
                    onChange={onChangeManager}
                    required
                />
                <label>Password</label>
                <input
                    type="password"
                    value={manager.password}
                    name="password"
                    onChange={onChangeManager}
                    required
                />
                <button type="submit">Register</button>
            </form>
                </div>
            }
        </div>
    )
}
