import React, { useState } from 'react'
import axios from 'axios'
import './Billing1.css';

export default function Billing() {
    const [ProductId, setProductId] = useState(0)
    const [productCount,setproductCount]=useState(0)
    const [User, setUser] = useState({
        name: "",
        address: "",
        list: [
            {
                id: 0,
                name: "",
                count: 0,
                price: 0
            }
        ],
        totalAmount: 0
    })

    const onchageUser = (event) => {
        const { name, value } = event.target
        setUser(prev => ({ ...prev, [name]: value }))
    }

    const onchageProductId = (event) => {
        setProductId(event.target.value)
    }
    const onchangeproductCount = (event) => {
        setproductCount(event.target.value)
    }
    const Handlesadd = (id) => {
        axios.get(`http://localhost:3000/product/${id}`)
            .then(res => {
                const product = res.data
                const newProduct = {
                    id: product.id,
                    name: product.name,
                    count: productCount,
                    price: product.price
                }

                // Update user's product list
                setUser(prev => {
                    const updatedList = [...prev.list, newProduct]
                    const updatedTotal = updatedList.reduce((sum, item) => sum + item.price * item.count, 0)
                    return {
                        ...prev,
                        list: updatedList,
                        totalAmount: updatedTotal
                    }
                })
            })
            .catch(err => {
                console.log(err)
            })
    }
    const handleDelete=(id)=>{
        const productList=User.list
        productList.map()
    }      

    return (
        <div className="container">
            <form onSubmit={e => e.preventDefault()}>
                <label>Name</label>
                <input value={User.name} name='name' type='text' onChange={onchageUser} /><br />
                <label>Address</label>
                <textarea name='address' value={User.address} onChange={onchageUser}></textarea><br />
                <label>ProductID</label>
                <input value={ProductId} onChange={onchageProductId} type='number' /><br></br>
                <label>ProductCount</label>
                <input type='number' value={productCount} onChange={onchangeproductCount}></input><br/>
                <button type='button' onClick={() => Handlesadd(ProductId)}>ADD</button>
            </form>

            <form>
                <h1>{User.name}</h1>
                <p>{User.address}</p>
            </form>

            <table>
                <thead>
                    <tr>
                        <th>ProductName</th>
                        <th>ProductPrice</th>
                        <th>ProductCount</th>
                        <th>Action</th>
                    </tr>
                </thead>
                <tbody>
                    {
                    User.list.slice(1).map((x, index) => (
                    <tr key={`${x.id}-${index}`}>
                        <td>{x.name}</td>
                        <td>{x.price}</td>
                        <td>{x.count}</td>
                        <button >Edit</button>
                        <button onClick={()=>handleDelete(x.id)}>Delete</button>
                    </tr>
                    ))
                    }
                </tbody>
            </table>

            <h2>Total: ₹{User.totalAmount}</h2>
        </div>
    )
}
