import React, {useEffect, useState } from 'react'
import axios from 'axios'

export default function Billing() {
    const[ProductId,setProductId]=useState(0)
    const[Product,setProduct]=useState({})
    const[newProduct,setnewProduct]=useState({
            id:0,
            name:"fggvb",
            count:1,
            price:10
        })
    const [User,setUser]=useState({
        name:"pradeep",
        address:"ertyuioihgfc",
        list:[{
            id:0,
            name:"fggvb",
            count:1,
            price:10
        },
    ],
        totalAmount:10
    })
    const onchageUser=(event)=>{
        const{name,value}=event.target
        setUser(prev=>({...prev,[name]:value}))
    }
    const onchageProductId=(event)=>{
        setProductId(event.target.value)
    }
    const Handlesadd=(id)=>{
        axios.get(`http://localhost:3000/product/${id}`)
        .then(res=>{
            setProduct(res.data)
        })
        .catch(err=>{
            console.log(err)
        })
        handleCountName(Product)
    }
    const handleCountName=(p)=>{
        const{id,name,price}=p
        setnewProduct({id,name,price})
    }
  return (
    <div>
        <form >
        <label>Name</label>
        <input value={User.name} name='name' type='text' onChange={onchageUser}></input><br/>
        <label>Address</label>
        <textarea name='address' value={User.address} onChange={onchageUser}></textarea><br/>
        <label>ProductID</label>
        <input value={ProductId} onChange={onchageProductId}type='number'></input>
        <button type='submit' onChange={()=>Handlesadd(ProductId)}>ADD</button>
        </form>
        <form>
            <h1>{User.name}</h1>
            <p>{User.address}</p>
        </form>
        <table>
            <thead>
                <th>ProductName</th>
                <th>ProductPrice</th>
                <th>ProductCount</th>
            </thead>
            <tbody>
                {
                    User.list.map(x=>(
                        <tr key={x.id}>
                        <td>{x.name}</td>
                        <td>{x.price}</td>
                        <td>{x.count}</td>
                        </tr>

                    ))
                }
            </tbody>
        </table>
        <h2>{User.totalAmount}</h2>

    </div>
  )
}
