import axios from 'axios'
import React, { useEffect, useState } from 'react'

export default function Homepage() {
    const[login,setlogin]=useState(true)
    const[EmployeeLogin,setEmployeeLogin]=useState([])
    const[ManagerLogin,setManagerLogin]=useState([])

    const[Employee,setEmployee]=useState({
        id:"",
        password:""
    })
    const[Manager,setManager]=useState({
        id:"",
        password:""
    })
    const onChangeEmpolyee=(event)=>{
        const{name,value}=event.target
        setEmployee(prev=>({...prev,[name]:value}))
    }
    const onChangeManager=(event)=>{
        const{name,value}=event.target
        setManager(prev=>({...prev,[name]:value}))
    }
    //get
  const employeeget = () => {
    axios.get('http://localhost:3000/product')
      .then(res => {
        console.log(res)
        setEmployeeLogin(res.data)})
      .catch(err => console.log(err))
  }
  const Managerget=()=>{
        axios.get("http://localhost:3000/ManagerLogin")
        .then(res=>{
            setManagerLogin(res.data)
            console.log(res)
        })
        .catch(err=>{
            console.log(err)
        })
    }
    useEffect(()=>{
        employeeget()
        Managerget()
    },[login]
    )
    const handleEmployeeLogin=(event)=>{
        
        const found=Employee.find(x=>
            x.id===Employee.id && x.password===Employee.password
        )
        if(found){
            console.log(true)
        }
        else{
            console.log(false)
        }
    }
  return (
    <div>
        <h1>Home</h1>
        <button onClick={()=>{setlogin(true)
            employeeget()
         }}>Employee</button>
        <button onClick={()=>{setlogin(false)}}>Manager</button>
        <div>
            {
                login?<form>
                    <h1>EmployeeLogin</h1>
                    <label>EmployeeId</label>
                    <input  value={Employee.id} name='id' onChange={onChangeEmpolyee}></input>
                    <label>Password</label>
                    <input type='password' value={Employee.password} name='password'  onChange={onChangeEmpolyee}></input>
                    <button onClick={()=>{handleEmployeeLogin()}}>Login</button>
                </form>:
                <form>
                    <h1>ManagerLogin</h1>
                    <label>ManagerId</label>
                    <input value={Manager.id} name='id' onChange={onChangeManager}></input>
                    <label>Password</label>
                    <input type='password' value={Manager.password} name='password'  onChange={onChangeManager}></input>
                    <button>Login</button>
                </form>
            }
        </div>
    </div>
  )
}
