import axios from 'axios'
import React, { useState } from 'react'

export default function SignUppage() {
    const[employee,setEmployee]=useState({
        name:"",
        password:""
    })
    const onChangeEmployee=(event)=>{
        const{name,value}=event.target
        setEmployee(prev=>({...prev,[name]:value}))
    }
    const registerEmployee=()=>{
        axios.post("http://localhost:3000/EmployeeLogin",employee)
        .then(res=>{
            console.log(res)
            alert("Register succesfull")
        })
        .catch(err=>console.log())
    }
  return (
    <div>
        <form>
            <label>name</label>
            <input value={employee.name} name='name' onChange={onChangeEmployee}></input>
            <label>PassWord</label>
            <input type='password' value={employee.password} name='password' onChange={onChangeEmployee}></input>
            <button>Register</button>
        </form>
    </div>
  )
}
