import React, { useState } from 'react';
import axios from 'axios';
import './Billing1.css';

export default function Billing1() {
  const [ProductId, setProductId] = useState("");
  const [productCount, setProductCount] = useState(0);
  const [editMode, setEditMode] = useState(false);
  const [editId, setEditId] = useState(null);

  const [User, setUser] = useState({
    name: "",
    address: "",
    list: [],
    totalAmount: 0
  });

  const [paymentMode, setPaymentMode] = useState("normal"); // "normal" or "userId"
  const [payerId, setPayerId] = useState("");

  const onchageUser = (e) => {
    const { name, value } = e.target;
    setUser(prev => ({ ...prev, [name]: value }));
  };

  const HandlesAdd = (id) => {
    if (editMode) {
      const updatedList = User.list.map(item =>
        item.productId === editId ? { ...item, quantity: productCount } : item
      );
      const updatedTotal = updatedList.reduce((sum, item) => sum + item.totalPrice * item.quantity, 0);
      setUser(prev => ({ ...prev, list: updatedList, totalAmount: updatedTotal }));
      setEditMode(false);
      setEditId(null);
    } else {
      axios.get(`http://localhost:3000/product/${id}`)
        .then(res => {
          const product = res.data;
          const productStock = parseInt(product.total);
          const productPrice = parseInt(product.price);

          if (productStock === 0) {
            alert("Out of stock!");
            return;
          }

          if (productCount > productStock) {
            alert(`Only ${productStock} in stock`);
            return;
          }

          const newItem = {
            productId: product.id,
            productName: product.name,
            quantity: productCount,
            totalPrice: productPrice
          };

          const updatedList = [...User.list, newItem];
          const updatedTotal = updatedList.reduce((sum, item) => sum + item.totalPrice * item.quantity, 0);

          setUser(prev => ({
            ...prev,
            list: updatedList,
            totalAmount: updatedTotal
          }));

          // reduce stock
          axios.put(`http://localhost:3000/product/${id}`, {
            ...product,
            total: (productStock - productCount).toString()
          });
        });
    }
  };


  const handleDelete = (id) => {
    const updatedList = User.list.filter(item => item.productId !== id);
    const updatedTotal = updatedList.reduce((sum, item) => sum + item.totalPrice * item.quantity, 0);
    setUser(prev => ({ ...prev, list: updatedList, totalAmount: updatedTotal }));
  };

  const handleEdit = (item) => {
    setProductId(item.productId);
    setProductCount(item.quantity);
    setEditMode(true);
    setEditId(item.productId);
  };

  const handleSubmitBill = async () => {
    if (!User.name || !User.address || User.list.length === 0) {
      alert("Please complete the bill");
      return;
    }

    // If payment via user ID, validate payer and balance
    if (paymentMode === "userId") {
      if (!payerId) {
        alert("Please enter payer User ID");
        return;
      }

      try {
        const payerRes = await axios.get(`http://localhost:3000/users/${payerId}`);
        const payer = payerRes.data;

        if (!payer) {
          alert("Payer user not found");
          return;
        }

        if (payer.money < User.totalAmount) {
          alert("Payer has insufficient balance");
          return;
        }

        // Deduct money from payer and update on server
        await axios.put(`http://localhost:3000/users/${payerId}`, {
          ...payer,
          money: payer.money - User.totalAmount
        });

      } catch (error) {
        alert("Error processing payment");
        console.error(error);
        return;
      }
    }

    // Submit the bill as usual
    const billData = {
      name: User.name,
      address: User.address,
      products: User.list,
      totalAmount: User.totalAmount,
      date: new Date().toISOString(),
      paidBy: paymentMode === "userId" ? payerId : "Normal Payment"
    };

    axios.post("http://localhost:3000/sales", billData)
      .then(() => {
        alert("Bill submitted successfully");
        setUser({
          name: "",
          address: "",
          list: [],
          totalAmount: 0
        });
        setProductId("");
        setProductCount(0);
        setPayerId("");
        setPaymentMode("normal");
      })
      .catch(err => {
        alert("Error submitting bill");
        console.error(err);
      });
  };

  return (
    <div className="container">
      <form onSubmit={e => e.preventDefault()}>
        <label>Name</label>
        <input value={User.name} name='name' onChange={onchageUser} /><br />
        <label>Address</label>
        <textarea name='address' value={User.address} onChange={onchageUser}></textarea><br />
        <label>Product ID</label>
        <input value={ProductId} type='text' onChange={e => setProductId(e.target.value)} /><br />
        <label>Product Count</label>
        <input value={productCount} type='number' onChange={e => setProductCount(Number(e.target.value))} /><br />
        <button type="button" onClick={() => HandlesAdd(ProductId)}>{editMode ? "Update" : "Add"}</button>
      </form>

      <hr />

      <div>
        <label>
          <input
            type="radio"
            value="normal"
            checked={paymentMode === "normal"}
            onChange={() => setPaymentMode("normal")}
          /> Pay Normally
        </label>
        <label style={{ marginLeft: '1' }}>
          <input
            type="radio"
            value="userId"
            checked={paymentMode === "userId"}
            onChange={() => setPaymentMode("userId")}
          /> Pay Through User ID
        </label>
      </div>

      {paymentMode === "userId" && (
        <div style={{ marginTop: '10px' }}>
          <label>Payer User ID: </label>
          <input
            type="text"
            value={payerId}
            onChange={e => setPayerId(e.target.value)}
          />
        </div>
      )}

      <h2>Customer: {User.name}</h2>
      <h3>Address: {User.address}</h3>

      <table>
        <thead>
          <tr><th>Name</th><th>Price</th><th>Quantity</th><th>Actions</th></tr>
        </thead>
        <tbody>
          {User.list.map((item, i) => (
            <tr key={i}>
              <td>{item.productName}</td>
              <td>{item.totalPrice}</td>
              <td>{item.quantity}</td>
              <td>
                <button onClick={() => handleEdit(item)}>Edit</button>
                <button onClick={() => handleDelete(item.productId)}>Delete</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>

      <h2>Total: â‚¹{User.totalAmount}</h2>
      <button type="button" onClick={handleSubmitBill}>Submit Bill</button>
    </div>
  );
}
