import React, { useState } from 'react'

export default function Homepage() {
    const[login,setlogin]=useState(true)
    const[Employee,setEmployee]=useState({
        id:"",
        Name:"",
        password:""
    })
    const[Manager,setManager]=useState({
        id:"",
        Name:"",
        password:""
    })
    const onChangeEmpolyee=(event)=>{
        const{name,value}=event.target
        setEmployee(prev=>({...prev,[name]:value}))
    }
    const onChangeManager=(event)=>{
        const{name,value}=event.target
        setManager(prev=>({...prev,[name]:value}))
    }
  return (
    <div>
        <h1>Home</h1>
        <button onClick={()=>{setlogin(true)}}>Employee</button>
        <button onClick={()=>{setlogin(false)}}>Manager</button>
        <div>
            {
                login?<form>
                    <h1>EmployeeLogin</h1>
                    <label>EmployeeId</label>
                    <input  value={Employee.id} name='id' onChange={onChangeEmpolyee}></input>
                    <label>Password</label>
                    <input type='password' value={Employee.password} name='password'  onChange={onChangeEmpolyee}></input>
                    <button>Sign Up</button>
                </form>:
                <form>
                    <h1>ManagerLogin</h1>
                    <label>ManagerId</label>
                    <input value={Manager.id} name='id' onChange={onChangeManager}></input>
                    <label>Password</label>
                    <input type='password' value={Manager.password} name='password'  onChange={onChangeManager}></input>
                </form>
            }
        </div>
    </div>
  )
}
