import React, { useEffect, useState } from 'react'
import axios from 'axios'
import './Product.css'

export default function Product1() {
  const [productList, setProductList] = useState([])
  const [form, setForm] = useState({ name: '', total: 0, price: 0 })
  const [editId, setEditId] = useState(null)

  const fetchProducts = () => {
    axios.get('http://localhost:3000/product')
      .then(res => setProductList(res.data))
      .catch(err => console.log(err))
  }

  useEffect(() => {
    fetchProducts()
  }, [])

  const handleChange = (e) => {
    const { name, value } = e.target
    setForm(prev => ({ ...prev, [name]: value }))
  }

  const handleSubmit = (e) => {
    e.preventDefault()
    const endpoint = editId
      ? `http://localhost:3000/product/${editId}`
      : 'http://localhost:3000/product'

    const method = editId ? axios.put : axios.post

    method(endpoint, form)
      .then(() => {
        setForm({ name: '', total: 0, price: 0 })
        setEditId(null)
        fetchProducts()
      })
      .catch(err => console.log(err))
  }

  const handleEdit = (product) => {
    setForm({ name: product.name, total: product.total, price: product.price })
    setEditId(product.id)
  }

  const handleDelete = (id) => {
    axios.delete(`http://localhost:3000/product/${id}`)
      .then(fetchProducts)
      .catch(err => console.log(err))
  }

  return (
    <div className="product-container">
      <h2>In</h2>
      <form onSubmit={handleSubmit}>
        <input name="name" value={form.name} onChange={handleChange} placeholder="Product Name" />
        <input name="total" type="number" value={form.total} onChange={handleChange} placeholder="Total Quantity" />
        <input name="price" type="number" value={form.price} onChange={handleChange} placeholder="Price" />
        <button type="submit">{editId ? "Update" : "Add"}</button>
      </form>

      <table>
        <thead>
          <tr><th>ID</th><th>Name</th><th>Stock</th><th>Price</th><th>Actions</th></tr>
        </thead>
        <tbody>
          {productList.map(product => (
            <tr key={product.id}>
              <td>{product.id}</td><td>{product.name}</td><td>{product.total}</td><td>{product.price}</td>
              <td>
                <button type="button" onClick={() => handleEdit(product)}>Edit</button>
                <button type="button" onClick={() => handleDelete(product.id)}>Delete</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  )
}
