import React, { useEffect, useState } from 'react';
import axios from 'axios';

export default function UserPage() {
  const [user, setUser] = useState({ id: '', name: '', money: 0 });
  const [loading, setLoading] = useState(true);
  const [billingAmount, setBillingAmount] = useState('');
  const [addAmount, setAddAmount] = useState('');

  // Fetch user data on mount
  useEffect(() => {
    axios.get('http://localhost:3000/user')  // replace with your API endpoint
      .then(res => {
        setUser(res.data);
        setLoading(false);
      })
      .catch(err => {
        alert('Failed to fetch user data');
        setLoading(false);
      });
  }, []);

  // Handle billing: deduct money
  const handleBilling = () => {
    const amount = parseFloat(billingAmount);
    if (isNaN(amount) || amount <= 0) {
      alert('Enter a valid billing amount');
      return;
    }
    if (user.money < amount) {
      alert('Insufficient balance');
      return;
    }

    // Update money locally (or send to API)
    const newMoney = user.money - amount;
    setUser(prev => ({ ...prev, money: newMoney }));
    setBillingAmount('');

    // Optional: send update to backend
    // axios.put('http://localhost:3000/user', { ...user, money: newMoney })
  };

  // Handle adding money
  const handleAddMoney = () => {
    const amount = parseFloat(addAmount);
    if (isNaN(amount) || amount <= 0) {
      alert('Enter a valid amount to add');
      return;
    }

    const newMoney = user.money + amount;
    setUser(prev => ({ ...prev, money: newMoney }));
    setAddAmount('');

    // Optional: send update to backend
    // axios.put('http://localhost:3000/user', { ...user, money: newMoney })
  };

  if (loading) return <div>Loading...</div>;

  return (
    <div style={{ maxWidth: '400px', margin: '2rem auto', fontFamily: 'Arial' }}>
      <h2>User Info</h2>
      <p><strong>ID:</strong> {user.id}</p>
      <p><strong>Name:</strong> {user.name}</p>
      <p><strong>Balance:</strong> ${user.money.toFixed(2)}</p>

      <hr />

      <div>
        <h3>Bill User</h3>
        <input
          type="number"
          placeholder="Amount to bill"
          value={billingAmount}
          onChange={(e) => setBillingAmount(e.target.value)}
        />
        <button onClick={handleBilling}>Bill</button>
      </div>

      <div style={{ marginTop: '1rem' }}>
        <h3>Add Money</h3>
        <input
          type="number"
          placeholder="Amount to add"
          value={addAmount}
          onChange={(e) => setAddAmount(e.target.value)}
        />
        <button onClick={handleAddMoney}>Add</button>
      </div>
    </div>
  );
}
