import React, { useEffect, useState } from 'react';
import axios from 'axios';

export default function UserManager() {
  const [users, setUsers] = useState([]);
  const [loading, setLoading] = useState(true);

  // For adding a new user
  const [newUserName, setNewUserName] = useState('');
  const [newUserMoney, setNewUserMoney] = useState('');

  // For editing a user
  const [editingUserId, setEditingUserId] = useState(null);
  const [editingUserName, setEditingUserName] = useState('');
  const [editingUserMoney, setEditingUserMoney] = useState('');

  // Fetch users
  const fetchUsers = () => {
    setLoading(true);
    axios.get('http://localhost:3000/users')
      .then(res => {
        setUsers(res.data);
        setLoading(false);
      })
      .catch(() => {
        alert('Failed to fetch users');
        setLoading(false);
      });
  };

  useEffect(() => {
    fetchUsers();
  }, []);

  // Add new user
  const handleAddUser = () => {
    if (!newUserName || isNaN(parseFloat(newUserMoney)) || parseFloat(newUserMoney) < 0) {
      alert('Please enter valid name and non-negative money');
      return;
    }

    const user = {
      name: newUserName,
      money: parseFloat(newUserMoney),
      id: Date.now().toString()  // Simple unique ID (use backend-generated id if possible)
    };

    axios.post('http://localhost:3000/users', user)
      .then(() => {
        fetchUsers();
        setNewUserName('');
        setNewUserMoney('');
      })
      .catch(() => alert('Failed to add user'));
  };

  // Start editing a user
  const startEdit = (user) => {
    setEditingUserId(user.id);
    setEditingUserName(user.name);
    setEditingUserMoney(user.money);
  };

  // Cancel editing
  const cancelEdit = () => {
    setEditingUserId(null);
    setEditingUserName('');
    setEditingUserMoney('');
  };

  // Save edited user
  const saveEdit = () => {
    if (!editingUserName || isNaN(parseFloat(editingUserMoney)) || parseFloat(editingUserMoney) < 0) {
      alert('Please enter valid name and non-negative money');
      return;
    }

    axios.put(`http://localhost:3000/users/${editingUserId}`, {
      name: editingUserName,
      money: parseFloat(editingUserMoney),
      id: editingUserId
    })
      .then(() => {
        fetchUsers();
        cancelEdit();
      })
      .catch(() => alert('Failed to update user'));
  };

  if (loading) return <div>Loading users...</div>;

  return (
    <div style={{ maxWidth: '600px', margin: '2rem auto', fontFamily: 'Arial' }}>
      <h2>User Manager</h2>

      {/* Add User */}
      <div style={{ marginBottom: '2rem' }}>
        <h3>Add New User</h3>
        <input
          placeholder="Name"
          value={newUserName}
          onChange={e => setNewUserName(e.target.value)}
          style={{ marginRight: 10 }}
        />
        <input
          type="number"
          placeholder="Money"
          value={newUserMoney}
          onChange={e => setNewUserMoney(e.target.value)}
          style={{ marginRight: 10 }}
        />
        <button onClick={handleAddUser}>Add User</button>
      </div>

      {/* Users List */}
      <h3>Users</h3>
      <table border="1" cellPadding="8" style={{ width: '100%', borderCollapse: 'collapse' }}>
        <thead>
          <tr>
            <th></th>
            <th>Name</th>
            <th>Money</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {users.map(user => (
            editingUserId === user.id ? (
              <tr key={user.id}>
                <td>
                  <input
                    value={editingUserName}
                    onChange={e => setEditingUserName(e.target.value)}
                  />
                </td>
                <td>
                  <input
                    type="number"
                    value={editingUserMoney}
                    onChange={e => setEditingUserMoney(e.target.value)}
                  />
                </td>
                <td>
                  <button onClick={saveEdit}>Save</button>
                  <button onClick={cancelEdit} style={{ marginLeft: 8 }}>Cancel</button>
                </td>
              </tr>
            ) : (
              <tr key={user.id}>
                <td>{user.name}</td>
                <td>{user.name}</td>
                <td>${user.money.toFixed(2)}</td>
                <td>
                  <button onClick={() => startEdit(user)}>Edit</button>
                </td>
              </tr>
            )
          ))}
        </tbody>
      </table>
    </div>
  );
}
