import React, { useState } from 'react'
import axios from 'axios'
import './Billing1.css';

export default function Billing() {
    const [ProductId, setProductId] = useState(0)
    const [productCount, setProductCount] = useState(0)
    const [editMode, setEditMode] = useState(false)
    const [editId, setEditId] = useState(null)

    const [User, setUser] = useState({
        name: "",
        address: "",
        list: [
            {
                id: 0,
                name: "",
                count: 0,
                price: 0
            }
        ],
        totalAmount: 0
    })

    const onchageUser = (event) => {
        const { name, value } = event.target
        setUser(prev => ({ ...prev, [name]: value }))
    }

    const onchageProductId = (event) => {
        setProductId(Number(event.target.value))
    }

    const onchangeProductCount = (event) => {
        setProductCount(Number(event.target.value))
    }

    const HandlesAdd = (id) => {
        if (editMode) {
            // Update existing product
            setUser(prev => {
                const updatedList = prev.list.map(item =>
                    item.id === editId ? { ...item, count: productCount } : item
                )
                const updatedTotal = updatedList.reduce((sum, item) => sum + item.price * item.count, 0)
                return {
                    ...prev,
                    list: updatedList,
                    totalAmount: updatedTotal
                }
            })
            setEditMode(false)
            setEditId(null)
        } else {
            // Add new product
            axios.get(`http://localhost:3000/product/${id}`)
                .then(res => {
                    const product = res.data
                    const newProduct = {
                        id: product.id,
                        name: product.name,
                        count: productCount,
                        price: product.price
                    }

                    setUser(prev => {
                        const updatedList = [...prev.list, newProduct]
                        const updatedTotal = updatedList.reduce((sum, item) => sum + item.price * item.count, 0)
                        return {
                            ...prev,
                            list: updatedList,
                            totalAmount: updatedTotal
                        }
                    })
                })
                .catch(err => {
                    console.log(err)
                })
        }
    }

    const handleDelete = (id) => {
        setUser(prev => {
            const updatedList = prev.list.filter(item => item.id !== id)
            const updatedTotal = updatedList.reduce((sum, item) => sum + item.price * item.count, 0)
            return {
                ...prev,
                list: updatedList,
                totalAmount: updatedTotal
            }
        })
    }

    const handleEdit = (item) => {
        setProductId(item.id)
        setProductCount(item.count)
        setEditMode(true)
        setEditId(item.id)
    }

    return (
        <div className="container">
            <form onSubmit={e => e.preventDefault()}>
                <label>Name</label>
                <input value={User.name} name='name' type='text' onChange={onchageUser} /><br />
                <label>Address</label>
                <textarea name='address' value={User.address} onChange={onchageUser}></textarea><br />
                <label>Product ID</label>
                <input value={ProductId} onChange={onchageProductId} type='number' /><br />
                <label>Product Count</label>
                <input type='number' value={productCount} onChange={onchangeProductCount} /><br />
                <button type='button' onClick={() => HandlesAdd(ProductId)}>{editMode ? "Update" : "Add"}</button>
            </form>

            <form>
                <h1>{User.name}</h1>
                <p>{User.address}</p>
            </form>

            <table>
                <thead>
                    <tr>
                        <th>Product Name</th>
                        <th>Product Price</th>
                        <th>Product Count</th>
                        <th>Actions</th>
                    </tr>
                </thead>
                <tbody>
                    {
                        User.list.slice(1).map((item, index) => (
                            <tr key={`${item.id}-${index}`}>
                                <td>{item.name}</td>
                                <td>{item.price}</td>
                                <td>{item.count}</td>
                                <td>
                                    <button type="button" onClick={() => handleEdit(item)}>Edit</button>
                                    <button type="button" onClick={() => handleDelete(item.id)}>Delete</button>
                                </td>
                            </tr>
                        ))
                    }
                </tbody>
            </table>

            <h2>Total: â‚¹{User.totalAmount}</h2>
        </div>
    )
}
