import axios from 'axios'
import React, { useEffect, useState } from 'react'
impo

export default function Homepage() {
    const [login, setLogin] = useState(true)
    const [employeeLogin, setEmployeeLogin] = useState([])
    const [managerLogin, setManagerLogin] = useState([])

    const [employee, setEmployee] = useState({ id: "", password: "" })
    const [manager, setManager] = useState({ id: "", password: "" })

    const onChangeEmployee = (event) => {
        const { name, value } = event.target
        setEmployee(prev => ({ ...prev, [name]: value }))
    }

    const onChangeManager = (event) => {
        const { name, value } = event.target
        setManager(prev => ({ ...prev, [name]: value }))
    }
    const employeeGet = () => {
        axios.get('http://localhost:3000/EmployeeLogin')
            .then(res =>{ 
                setEmployeeLogin(res.data)
                console.log(res)
            })
            .catch(err => console.log(err))
    }

    const managerGet = () => {
        axios.get("http://localhost:3000/ManagerLogin")
            .then(res => {
                setManagerLogin(res.data)
                console.log(res)
            })
            .catch(err => console.log(err))
    }

    useEffect(() => {
        employeeGet()
        managerGet()
    }, [login])

    const handleEmployeeLogin = (event) => {
        event.preventDefault()
        const found = employeeLogin.find(x =>
            x.id === employee.id && x.password === employee.password
        )
        if (found) {
            console.log("Login successful")
        } else {
            console.log("Invalid credentials")
        }
    }
    const handleManagerLogin = (event) => {
        event.preventDefault()
        const found = managerLogin.find(x =>
            x.id === manager.id && x.password === manager.password
        )
        if (found) {
            console.log("Login successful")
        } else {
            console.log("Invalid credentials")
        }
    }
    return (
        <div className="container">
            <h1>Home</h1>
            <div className="button-group">
                <button onClick={() => setLogin(true)}>Employee</button>
                <button onClick={() => setLogin(false)}>Manager</button>
            </div>
            <div>
                {
                    login ? (
                        <form onSubmit={handleEmployeeLogin}>
                            <h1>Employee Login</h1>
                            <label>Employee ID</label>
                            <input value={employee.id} name='id' onChange={onChangeEmployee} />
                            <label>Password</label>
                            <input type='password' value={employee.password} name='password' onChange={onChangeEmployee} />
                            <button type="submit">Login</button>
                        </form>
                    ) : (
                        <form onSubmit={handleManagerLogin}>
                            <h1>Manager Login</h1>
                            <label>Manager ID</label>
                            <input value={manager.id} name='id' onChange={onChangeManager} />
                            <label>Password</label>
                            <input type='password' value={manager.password} name='password' onChange={onChangeManager} />
                            <button type="submit">Login</button>
                        </form>
                    )
                }
            </div>
        </div>
    )
}
